    /*global chrome*/

import React, { useState, useEffect } from 'react';

const AdminMessages = () => {
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    // Fetch messages from Chrome storage or an external source

    // chrome.storage.local.get(['adminMessages'], (result) => {
    //   console.log("print message stored in chrome")
    //   console.log(result)
    //   console.log("proceed")
    //   result.adminMessages.map((msg, index) => {
    //     console.log("print individual messages")
    //     console.log(msg.content)
    //   })
    // });


    chrome.storage.local.get(['adminMessages'], (result) => {
        setMessages(result);       
    });
  }, []
);

  return (
    <div>
      <h3>Admin Messages</h3>
      <ul>
        {messages.length > 0 ? (
          messages.adminMessages.map((msg, index) => <li key={index}>{msg.content}</li>)
        ) : (
          <li>No new messages</li>
        )}
      </ul>
    </div>
  );
};

export default AdminMessages;
